mapper.update:
UPDATE ai_client
SET client_name = #{clientName},
    description = #{description},
    status      = #{status},
    update_time = #{updateTime}
WHERE client_id = #{clientId}

UPDATE ai_client
SET client_name = #{clientName},
    description = #{description},
    status      = #{status},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT id, client_id, client_name, description, status, create_time, update_time
FROM ai_client
WHERE client_id = #{clientId}

SELECT id, client_id, client_name, description, status, create_time, update_time
FROM ai_client
WHERE id = #{id}

SELECT id, client_id, client_name, description, status, create_time, update_time
FROM ai_client
WHERE client_name = #{clientName}
