mapper.select:
SELECT id, mcp_id, mcp_name, transport_type, transport_config, request_timeout, status, create_time, update_time
FROM ai_client_tool_mcp
WHERE mcp_id = #{mcpId}

SELECT id, mcp_id, mcp_name, transport_type, transport_config, request_timeout, status, create_time, update_time
FROM ai_client_tool_mcp
WHERE id = #{id}

SELECT id, mcp_id, mcp_name, transport_type, transport_config, request_timeout, status, create_time, update_time
FROM ai_client_tool_mcp
WHERE status = #{status}
